import { useState } from "react";
import React from "react";
const Navigation = () => {
    const [hiddenMenu, setHiddenMenu] = useState(true);
    const ref = React.useRef();
    const ToggleMenu = () => {
        hiddenMenu
        ? setHiddenMenu(false) : setHiddenMenu(true) 
    }
    const closeMenu = () => {
        setHiddenMenu()
    }
    return (
        <article className="side-menu">
            <NavigationHmbrg ToggleMenu={ToggleMenu} />
            <section className={`main-menu${hiddenMenu ? `` : ` active-menu`}`}>
                <NavigationLogo closeMenu={closeMenu} />
                <NavigationMenu closeMenu={closeMenu} />
                <NavigationSocials />
                <NavigationEmail />
            </section>
        </article>
    );
};

export default Navigation;
